fetch-query.injection-script:
                
				(function(r,frs){ 
					var ArrayList = Java.type('java.util.ArrayList');
					var items = new ArrayList();
					for each (var fr in frs){
						if(r.get("id").equals(fr.get("orderId"))){
							items.add(fr);
						}
					}
					r.put("items",items);
				})(r,frs);
